
public class SignOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		 for( int i = 1; i<=100 ;i++){
			//for 코드를 통해 1부터 100까지 출력하는 문장 작성
			      if(i%2==0){
			//for 블럭 내에서 if블럭을 이용해서 짝수만 출력
			      System.out.println(i);
			      }
			    }
	}
		
	}


