package day01;
/*
 *  비트연산자
 *    
 *    &(and)
 *    |(or)
 *    ~(not)
 *    ^(xor)
 * 
 */
public class OperatorTest6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		  int su=10;
		  int su2=5;
		  
		  System.out.println(su&su2);
		  System.out.println(su|su2);
		  System.out.println(~su);
		  System.out.println(su^su2);
		
		
		
	}

}
