package day01;

/*
 *   변수 :  자료를 담는 그릇
 *   
 *   기본타입 (prmitive type)
 *   <---- 정수형                      -----><--  실수형  -----> 
 *                         10L or 10l    10.2f 10.2F
 *             2byte   4byte  8byte   
 *    byte    short   int      long          float   double  
 *   
 *             char  (문자형)  ->int로 형변환 가능
 *             
 *   boolean(true/false)
 *   
  *    참조타입 (reference type)
 *
 *
 *     byte  =>8bit   0,1=> bit 
 *
 *
 */


public class Variable_Test1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    
	     byte  a=10;
	     short b=20;
	     int c=30;
	     long d=10L;
	     char   ch='a';
	     
	      System.out.println(a);
         System.out.println(b);
         System.out.println(c);
         System.out.println(d);
		 System.out.println(ch);
		  
		
	}

}
